var requestRunner = (function () {
  var requestRunnerWrapper = (function () {
    var elem = document.createElement('div');
    elem.id = 'requestRunner';
    elem.classList.add('requestRunner-close');
    return elem;
  })();
  var requestRunnerTab = (function () {
    var elem = document.createElement('div');
    elem.id = 'requestRunnerTab';
    elem.classList.add('requestRunner');
    return elem;
  })();
  var requestRunnerTabHeader = (function () {
    var elem = document.createElement('div');
    elem.id = 'requestRunnerTabHeader';
    elem.textContent = 'Request Runner';
    return elem;
  })();
  var requestRunnerTabClose = (function () {
    var elem = document.createElement('div');
    elem.id = 'requestRunnerTabClose';
    elem.innerHTML = '&times;';
    return elem;
  })();
  var requestRunnerFrameWrapper = (function () {
    var elem = document.createElement('div');
    elem.id = 'requestRunnerFrameWrapper';
    return elem;
  })();
  var requestRunnerLoader = (function () {
    var elem = document.createElement('div');
    elem.classList.add('requestRunnerLoader');
    elem.classList.add('ui');
    elem.classList.add('active');
    elem.classList.add('indeterminate');
    elem.classList.add('text');
    elem.textContent = 'Loading';
    elem.classList.add('loader');
    return elem;
  })();
  var requestRunnerFrame = (function () {
    var elem = document.createElement('iframe');
    elem.id = 'requestRunnerframe';
    elem.classList.add('requestRunner-close');
    elem.onload = function () {
      setTimeout(function () {
        requestRunnerFrame.classList.add('requestRunnerShow');
      }, 500);
    };
    elem.src = '';
    return elem;
  })();
  var requestRunnerSearch = function () {
    var elem = document.createElement('input');
    elem.placeholder = 'Search...';
    elem.id = 'algolia-search-frame';
    elem.onload = function () {
      docsearch({
        appId: 'QUTLQTIH9V',
        apiKey: 'd380da466100e42097fe7c280d5cc5e8',
        indexName: 'stoplight_API_Endpoints',
        inputSelector: '#algolia-search-frame',
        debug: true,
        algoliaOptions: {
          hitsPerPage: 10,
          facetFilters: ['hierarchy.lvl6:-Host'],
        },
      });
    };
    return elem;
  };

  var requestRunnerOpen = false;
  var lastRequestSrc = '';
  var newRequestSrc = '';

  var newFrameLoading = function () {
    return newRequestSrc != lastRequestSrc;
  };

  var pageReady = (callback) => {
    if (document.readyState != 'loading') callback();
    else document.addEventListener('DOMContentLoaded', callback);
  };

  var addToggleEvent = function () {
    if (window.top != window.self) {
      return;
    }
    document.querySelectorAll('a[href*="#requestrunner"]').forEach((e) => {
      e.onclick = function () {
        requestRunnerFrame.classList.remove('requestRunnerShow');
        lastRequestSrc = requestRunnerFrame.src;
        newRequestSrc = e.href;

        setTimeout(function () {
          requestRunnerFrame.src = newRequestSrc;
        }, 300);

        toggleRequestRunner();
        return false;
      };
    });
  };

  var toggleRequestRunner = function () {
    if (!requestRunnerOpen && newFrameLoading()) {
      requestRunnerOpen = true;
      requestRunnerWrapper.classList.remove('requestRunner-close');
      requestRunnerFrame.classList.remove('requestRunner-close');
      requestRunnerWrapper.classList.add('requestRunner-expand');
      requestRunnerFrame.classList.add('requestRunner-expand');
      return;
    }
    if (requestRunnerOpen && !newFrameLoading()) {
      requestRunnerOpen = false;
      requestRunnerWrapper.classList.add('requestRunner-close');
      requestRunnerFrame.classList.add('requestRunner-close');
      requestRunnerWrapper.classList.remove('requestRunner-expand');
      requestRunnerFrame.classList.remove('requestRunner-expand');
      return;
    }
    if (!requestRunnerOpen && !newFrameLoading()) {
      requestRunnerOpen = true;
      requestRunnerFrame.classList.add('requestRunnerShow');
      requestRunnerWrapper.classList.remove('requestRunner-close');
      requestRunnerFrame.classList.remove('requestRunner-close');
      requestRunnerWrapper.classList.add('requestRunner-expand');
      requestRunnerFrame.classList.add('requestRunner-expand');
      return;
    }
    if (requestRunnerOpen && newFrameLoading()) {
      requestRunnerOpen = true;
      return;
    }
  };

  var insertRequester = function () {
    if (window.top != window.self) {
      return;
    }
    requestRunnerTab.append(requestRunnerTabHeader);
    requestRunnerTab.append(requestRunnerTabClose);
    requestRunnerFrameWrapper.append(requestRunnerTab);
    requestRunnerFrameWrapper.append(requestRunnerFrame);
    requestRunnerFrameWrapper.append(requestRunnerLoader);
    requestRunnerWrapper.append(requestRunnerFrameWrapper);
    document.body.append(requestRunnerWrapper);
  };

  var init = function () {
    hideStuffInIframe();
    if (window.top != window.self) {
      return;
    }
    requestRunnerTab.onclick = function () {
      requestRunnerOpen = false;
      lastRequestSrc = requestRunnerFrame.src;
      setTimeout(function () {
        requestRunnerFrame.classList.remove('requestRunnerShow');
      }, 200);
      requestRunnerWrapper.classList.add('requestRunner-close');
      requestRunnerFrame.classList.add('requestRunner-close');
      requestRunnerWrapper.classList.remove('requestRunner-expand');
      requestRunnerFrame.classList.remove('requestRunner-expand');
    };
    insertRequester();
    addToggleEvent();
  };

  var hideStuffInIframe = function () {
    if (inIframe()) {
      document.body.classList.add('requestRunnerHub');
    }
  };

  var inIframe = function () {
    try {
      return window.self !== window.top;
    } catch (e) {
      return true;
    }
  };

  return {
    init: init,
    pageReady: pageReady,
    addToggleEvent: addToggleEvent,
  };
})();

