  const hideWebhookModels = () => {
    const targetNode = document.body;

    // Options for the observer (which mutations to observe)
    const config = {
      attributes: false,
      characterData: false,
      childList: true,
      subtree: true,
    };

    xpath = document.evaluate(
      "//div[text()='Models']",
      document.body,
      null,
      XPathResult.ORDERED_NODE_SNAPSHOT_TYPE,
      null
    );

    if (xpath.snapshotItem(0)) {
      xpath.snapshotItem(0).innerText = 'Callbacks';
    }

    // Callback function to execute when mutations are observed
    const callback = function (mutationsList, observer) {
      for (const mutation of mutationsList) {
        if (mutation.type === 'childList') {
          const addedNodes = Array.from(mutation.addedNodes);
          const foundNode = addedNodes.some((node) =>
            node.parentNode.outerText.includes('Models')
          );

          const foundNode1 = addedNodes.some((node) =>
            node.parentNode.outerText.includes('Callbacks')
          );

          if (addedNodes && (foundNode || foundNode1)) {
            var headings = document.evaluate(
              "//div[text()='Models']",
              document.body,
              null,
              XPathResult.ANY_TYPE,
              null
            );

            let node = null;

            while ((node = headings.iterateNext())) {
              node.innerText = 'Callbacks';
            }

            // observer.disconnect();

            function listOfElmsByXpath(xpath) {
              const result = document.evaluate(
                xpath,
                document.body,
                null,
                XPathResult.ANY_TYPE,
                null
              );

              let node = null;

              while ((node = result.iterateNext())) {
                node.style.display = 'none';
              }
            }

            let nodeArray = [
              ".//A[contains(@href, 'models/pagination')]",
              ".//A[contains(@href, 'models/callback-category-data')]",
              ".//A[contains(@href, 'models/errordetailed-full')]",
              ".//A[contains(@href, 'models/error-full')]",
              ".//A[contains(@href, 'models/error-full')]",
              ".//A[contains(@href, 'models/webhook-base')]",
              ".//A[contains(@href, 'models/webhook-full')]",
              ".//A[contains(@href, 'models/webhook-callback-base')]",
            ];

            nodeArray.forEach((xpath) => listOfElmsByXpath(xpath));
          }
          continue;
        }
      }
    };

    // Create an observer instance linked to the callback function
    const observer = new MutationObserver(callback);

    // Start observing the target node for configured mutations
    observer.observe(targetNode, config);
  };



